void CCSurfacesVertexInput(out SurfacesStandardVertexIntermediate In)
{
  In.position = vec4(a_position, 1.0);
  In.normal = a_normal;
#if CC_SURFACES_USE_TANGENT_SPACE
  In.tangent = a_tangent;
#endif
